import React, { useState } from 'react'

function Clock() {
    let time = new Date().toLocaleString()
    let year = time.getFullYear()
    const [Ctime, setCtime] = useState(time)
    const updateTime = () => {
        time = new Date().toLocaleString()
        setCtime(time)
    }
    setInterval(updateTime, 1000)
    return (
        <div>
            {Ctime}
        </div>
    )
}

export default Clock
