import React, { useEffect } from 'react'
import { Row, Col, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'

import { listItems } from '../actions/itemActions'
import Loader from '../components/Loader'
import Message from '../components/Message'
import Item from '../components/Item'


function ItemListScreen() {

    let page = 1
    let params = {
        params: {
            page: page + 1
        }
    }

    const dispatch = useDispatch()

    const itemList = useSelector(state => state.itemList)
    const {loading, error, items, previous, next, count} = itemList

    useEffect(() => {
        dispatch(listItems())
    }, [dispatch])

    const prevHandler = () => {

        dispatch(listItems(params))
        page++
    }

    const nextHandler = () => {
        params.params.page += 1
        dispatch(listItems(params))
        page--
    }

    return (
        <div>
            <Row>
                <Col>
                    <h2>Items</h2>
                    {loading ? <Loader />
                        : error ? <Message variant='danger'>{error}</Message>
                            : items.length === 0 ? <Message variant='info'>No Items</Message>
                                : <div>
                                    <Row>
                                        {items.map(item => (
                                            <Col key={item.id} sm={12} md={6} lg={4} xl={3}>
                                                <Item item={item} />
                                            </Col>
                                        ))}
                                    </Row>
                                  </div>
                    }
                </Col>
            </Row>
            {previous && <Button onClick={prevHandler}>prev</Button>}
            {next && <Button onClick={nextHandler}>next</Button>}
        </div>
    )
}

export default ItemListScreen
