import axios from 'axios'
import { 
    ITEM_LIST_REQUEST,
    ITEM_LIST_SUCCESS,
    ITEM_LIST_FAIL,
 } from '../constants/itemConstants'


export const listItems = (page_url=null) => async (dispatch) => {
    try{
        dispatch({
            type: ITEM_LIST_REQUEST
        })

        const {data} = null

        if (page_url) {
            data = await axios.get(page_url)
        } else {
            data = await axios.get(`/api/item/list/`)
        }

        dispatch({
            type: ITEM_LIST_SUCCESS,
            payload: data,
        })
    } catch (error) {
        dispatch({
            type: ITEM_LIST_FAIL,
            payload: error.response && error.response.data.detail
                ? error.response.data.detail
                    : error.message,
        })
    }
}
