import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col, Image, Button, ListGroup, Form } from 'react-bootstrap';

import AuctionService from '../services/AuctionService'
import Loader from '../components/Loader'
import Message from '../components/Message'
import { STATUS, TYPE, getKeyByValue } from '../constants/auctionConstants'


function AuctionDetailScreen({match, history}) {
    const roomName = match.params.id
    const socket = new WebSocket(
            'ws://'
            + window.location.host
            + '/ws/auction/'
            + roomName
            + '/'
        );
    console.log('socket status:', socket.readyState)
    console.log('window.location.host:', window.location.host)
    // socket.addEventListener('message', function (event) {
    //     console.log('Message from server ', event.data);
    // });
    socket.onmessage = (event) => {
        let data = JSON.parse(event.data);
        console.log('From server', data);
    }

    const auctionService = new AuctionService()

    const [price, setPrice] = useState(0)

    const dispatch = useDispatch()

    const auctionDetail = useSelector(state => state.auctionDetail)
    const {loading, auction, error} = auctionDetail

    useEffect(() => {
        dispatch(auctionService.getAuctionDetail(match.params.id))
    }, [dispatch, match])

    const makeOfferHandler = () => {
        console.log('Making offer')
    }

    return (
        <div>
            <Button className="btn btn-dark my-3" onClick={() => history.goBack()}>
                Back
            </Button>
            {loading ? <Loader />
                : error ? <Message variant='danger'>{error}</Message>
                    : (
                        <div>
                            <Row>
                                <Col md={6}>
                                    <Image src={auction.lot.item.photo} alt={auction.lot.item.title} fluid />
                                </Col>
                                <Col md={6}>
                                    <ListGroup variant="flush">
                                        <ListGroup.Item>
                                            <h3>Type: {getKeyByValue(TYPE, auction.type)}</h3>
                                        </ListGroup.Item>
                                        <ListGroup.Item>
                                            <h3>Status: {getKeyByValue(STATUS, auction.auction_status)}</h3>
                                        </ListGroup.Item>
                                        <ListGroup.Item>
                                            <h3>Start price: ${auction.start_price}</h3>
                                        </ListGroup.Item>
                                        <ListGroup.Item>
                                            <h3>Current offer: ${auction.end_price}</h3>
                                        </ListGroup.Item>
                                        <ListGroup.Item>
                                            <h3>Min price step: ${auction.price_step}</h3>
                                        </ListGroup.Item>
                                        <ListGroup.Item>
                                            <h3>Starting date: {auction.opening_date}</h3>
                                        </ListGroup.Item>
                                        <ListGroup.Item>
                                            <h3>Closing date: {auction.closing_date}</h3>
                                        </ListGroup.Item>
                                    </ListGroup>
                                </Col>
                            </Row>
                            <Row>
                                <Col>Your offer:</Col>
                                <Col>
                                    <Form.Control
                                        type='number'
                                        value={price}
                                        onChange={(e) => setPrice(e.target.value)}
                                    >
                                    </Form.Control>
                                    <Button
                                        onClick={makeOfferHandler}
                                        className='btn-block'
                                        disabled={auction.auction_status !== STATUS.IN_PROGRESS}
                                    >
                                        Submit
                                    </Button>
                                </Col>
                            </Row>
                        </div>
                    )}
        </div>
    )
}

export default AuctionDetailScreen
