import React, { useState } from 'react'

function Clock() {
    let date = new Date()
    let datetime = {
        year: date.getFullYear(),
        month: date.getMonth(),
        day: date.getDay(),
        hours: date.getHours(),
        minutes: date.getMinutes(),
        seconds: date.getSeconds(),
    }
    const [Ctime, setCtime] = useState(datetime)
    const updateTime = () => {
        time = new Date().toLocaleString()
        setCtime(time)
    }
    setInterval(updateTime, 1000)
    return (
        <div>
            {Ctime}
        </div>
    )
}

export default Clock
